mapper.insert:
INSERT INTO product (
    id,name, category_id, tags, platform_division,
    associated_prod_ids, coupon_discount_id,
    shiping_fee_temp_id, shop_id, spu, sku
)
VALUES (
           #{id},#{name}, #{categoryId}, #{tags}, #{platformDivision},
           #{associatedProdIds}, #{couponDiscountId},
           #{shipingFeeTempId}, #{shopId}, #{spu}, #{sku}
       )

mapper.update:
UPDATE product
name = #{name}, category_id = #{categoryId}, tags = #{tags}, platform_division = #{platformDivision}, associated_prod_ids = #{associatedProdIds}, coupon_discount_id = #{couponDiscountId}, shiping_fee_temp_id = #{shipingFeeTempId}, shop_id = #{shopId}, spu = #{spu}, sku = #{sku}
WHERE id = #{id}

mapper.select:
select p.id as product_id, p.name as product_name, p.tags as product_tags, p.category_id as product_category_id, p.spu->'$.mainImage' as spu_mainImage, o.price as onSale_price from product p join onsale o on p.id=o.product_id
AND p.name LIKE CONCAT('%', #{name}, '%')
AND p.category_id = #{categoryId}
