mapper.insert:
INSERT INTO product (
    id,name, category_id, specs, tags, platform_division,
    imags, associated_prod_ids, coupon_discount_id,
    shiping_fee_temp_id, shop_id
)
VALUES (
           #{id},#{name}, #{categoryId}, #{specs}, #{tags}, #{platformDivision},
           #{imags}, #{associatedProdIds}, #{couponDiscountId},
           #{shipingFeeTempId}, #{shopId}
       )

mapper.update:
UPDATE product
name = #{name}, category_id = #{categoryId}, specs = #{specs}, tags = #{tags}, platform_division = #{platformDivision}, imags = #{imags}, associated_prod_ids = #{associatedProdIds}, coupon_discount_id = #{couponDiscountId}, shiping_fee_temp_id = #{shipingFeeTempId}, shop_id = #{shopId}
WHERE id = #{id}
